local function CmdHandler(msg)
	local de = string.match(msg, "%[(.*)%]")
	local quest = Quests[de]

	ChatFrame3:Clear()

	if quest == nil then
		ChatFrame3:AddMessage("Квест не найден", 1, 0, 0)
		return
	end
	
	ChatFrame3:AddMessage(quest.Title, 1, 1, 0)
	ChatFrame3:AddMessage(quest.Description, 1.0, 0.8, 0.6)
	
	if string.len(quest.Progress) > 0 then
		ChatFrame3:AddMessage("\n")
		ChatFrame3:AddMessage("Прогресс", 0, 1, 1)
		ChatFrame3:AddMessage(quest.Progress, 1.0, 0.8, 0.6)
	end

    if string.len(quest.Complete) > 0 then
	   ChatFrame3:AddMessage("\n")
	   ChatFrame3:AddMessage("Завершение", 0, 1, 1)
	   ChatFrame3:AddMessage(quest.Complete, 1.0, 0.8, 0.6)
    end
end

local function OnEvent()
	Addon:UnregisterEvent("ADDON_LOADED")
	SlashCmdList["X"] = CmdHandler
    SLASH_X1 = "/x"
end

Addon:SetScript("OnEvent", OnEvent)
Addon:RegisterEvent("ADDON_LOADED")